    private void searchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchActionPerformed
        // TODO add your handling code here:
        try
        {
            stmt = con.createStatement();

            int sId = Integer.parseInt(txt_id.getText());

            String query = "SELECT * FROM subject";

            rs = stmt.executeQuery(query);

            while(rs.next())
            {
                if(sId == rs.getInt("id"))
                {
                    txt_name.setText(rs.getString("subjectname"));
                    txt_tname.setText(rs.getString("teachername"));
                    
                }
            }
        }
        catch(Exception e)
        {
            System.out.println(e);
        }
    }//GEN-LAST:event_searchActionPerformed

    private void updateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updateActionPerformed
       try{
           int selection = JOptionPane.showConfirmDialog(rootPane,"want to change subject");
           if(selection == 0){
              DB.getConnection().createStatement().executeUpdate("update subject set subjectname='"+txt_name.getText()+"',teachername='"+txt_tname.getText()+"' where id='"+txt_id+"'");
       JOptionPane.showMessageDialog(rootPane, "DATA UPDATED SUCCESSFULY");
       loadData();
           }
       
          
       }catch(Exception e){
           System.out.println(e);
       }
        

    }//GEN-LAST:event_updateActionPerformed

