void loadDta(){
    try{
       
    
    ResultSet rs = DB.getConnection().createStatement().executeQuery("select * from subject");
    while(rs.next()){
        Vector v = new Vector();
        v.add(rs.getInt("id"));
        v.add(rs.getString("subjectname"));
        v.add(rs.getString("teachername"));
      
         
    }
        
    }catch(Exception e){
            System.out.println(e);
            }
    }

